const INITIAL_STATE = {};

  const reducer = (state = INITIAL_STATE, action) => { // Como padr達o passamos para o reducer o state e a action.
    switch (action.type) { // Usamos o Switch para tratar os casos das actions.
    case 'ADD_CLIENT':
      return {
        ...state, // Usamos o spread para conservar os outros dados do store que n達o iremos mudar nesse momento.
        clients: action.clients,
      }
    default:
      return state;
    }
  };

  export default reducer;
