import React from 'react';
import { connect } from 'react-redux'; // Importamos o conect par conectar o React com o Redux.
import addRegiter from '../actions/index'; // Importamos a action para fazermos os dispatchs.

class LogInPage extends React.Component {
  constructor(props) {
    super(props)

    this.handleChange = this.handleChange.bind(this)

    this.state = {  // Setamos um state local para depois mandarmos o todas as informações referente a esse form de uam vez só.
      email: '',
      senha: '',
    }
  }

  handleChange(event) { // Uma função com a lógica de subir os dados dos inputs para o state.
    let { name, value } = event.target

    this.setState({
        [name]: value
    })
  }

  render() {
    return (
      <div>
        <label>
            Email:
            <input
              name="email"
              type="text"
              maxLength="50"
              value={this.state.email}
              onChange={this.handleChange}
            />
          </label>
          <label>
            Senha:
            <input
              name="senha"
              type="number"
              maxLength="50"
              value={this.state.senha}
              onChange={this.handleChange}
            />
          </label>
      </div>
    )
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(LogInPage); // Passamos o connect no export para unirmos o store ao component.
