const INITIAL_STATE = []

const signUpReducer = ( state = INITIAL_STATE, action ) => { // Como padrão passamos para o reducer o state e a action.
  switch (action.type) { // Usamos o Switch para tratar os casos das actions.
    case "ADD_REGISTER":
      return [
        ...state, // Já que é uma lista onde vão ter varios cadástros, usamos o spread.
        action.data,
      ]
    default:
      return state;
  }  
}

export default signUpReducer;
